'use client';

import React, { useEffect, useState } from 'react';
import { RepresentativeClient } from './RepresentativeClient';
import { EnhancedRepresentative } from '@/types/representative';

interface DataFetchingWrapperProps {
  representative: EnhancedRepresentative;
  initialServerData?: {
    bills?: unknown[];
    votes?: unknown[];
    finance?: Record<string, unknown>;
    news?: unknown[];
  };
  partialErrors?: Record<string, string>;
}

export function DataFetchingWrapper({
  representative,
  initialServerData,
  partialErrors: initialPartialErrors,
}: DataFetchingWrapperProps) {
  const [serverData, setServerData] = useState(initialServerData);
  const [partialErrors, setPartialErrors] = useState(initialPartialErrors || {});
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    // Only fetch if we don't have initial data
    if (!initialServerData?.bills?.length || !initialServerData?.votes?.length) {
      fetchData();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [representative.bioguideId, initialServerData]);

  const fetchData = async () => {
    setIsLoading(true);
    const newServerData = {
      bills: initialServerData?.bills || [],
      votes: initialServerData?.votes || [],
      finance: initialServerData?.finance || {},
      news: initialServerData?.news || [],
    };
    const newPartialErrors: Record<string, string> = {};

    // Fetch bills if not already available
    if (!initialServerData?.bills?.length) {
      try {
        const billsResponse = await fetch(`/api/representative/${representative.bioguideId}/bills`);
        if (billsResponse.ok) {
          const billsData = await billsResponse.json();
          // Extract the bills array from the response structure
          newServerData.bills = Array.isArray(billsData?.sponsored?.bills)
            ? billsData.sponsored.bills
            : Array.isArray(billsData?.sponsoredLegislation)
              ? billsData.sponsoredLegislation
              : [];
        } else {
          newPartialErrors.bills = `HTTP ${billsResponse.status}`;
        }
      } catch (error) {
        newPartialErrors.bills = error instanceof Error ? error.message : 'Failed to fetch';
      }
    }

    // Fetch votes if not already available
    if (!initialServerData?.votes?.length) {
      try {
        const votesResponse = await fetch(`/api/representative/${representative.bioguideId}/votes`);
        if (votesResponse.ok) {
          const votesData = await votesResponse.json();
          // Extract the votes array from the response structure
          newServerData.votes = Array.isArray(votesData?.votes) ? votesData.votes : [];
        } else {
          newPartialErrors.votes = `HTTP ${votesResponse.status}`;
        }
      } catch (error) {
        newPartialErrors.votes = error instanceof Error ? error.message : 'Failed to fetch';
      }
    }

    setServerData(newServerData);
    setPartialErrors(newPartialErrors);
    setIsLoading(false);
  };

  // Show loading state while fetching
  if (isLoading && (!initialServerData?.bills?.length || !initialServerData?.votes?.length)) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading representative data...</p>
        </div>
      </div>
    );
  }

  return (
    <RepresentativeClient
      representative={representative}
      serverData={serverData}
      partialErrors={partialErrors}
    />
  );
}

