/**
 * Enhanced Representative Types
 *
 * This file defines comprehensive types for representative data,
 * combining our existing structure with congress-legislators enhancements.
 * Includes error handling and batch API response types.
 */

// Base representative interface (existing structure)
export interface BaseRepresentative {
  bioguideId: string;
  name: string;
  firstName: string;
  lastName: string;
  party: string;
  state: string;
  district?: string;
  chamber: 'House' | 'Senate';
  title: string;
  phone?: string;
  email?: string;
  website?: string;
  imageUrl?: string;
  terms: Array<{
    congress: string;
    startYear: string;
    endYear: string;
  }>;
  committees?: Array<{
    name: string;
    role?: string;
  }>;
}

// Enhanced representative with congress-legislators data
export interface EnhancedRepresentative extends BaseRepresentative {
  // Enhanced name information
  fullName?: {
    first: string;
    middle?: string;
    last: string;
    suffix?: string;
    nickname?: string;
    official?: string;
  };

  // Biographical information
  bio?: {
    birthday?: string;
    gender?: 'M' | 'F';
    religion?: string;
  };

  // Current term details
  currentTerm?: {
    start: string;
    end: string;
    office?: string;
    phone?: string;
    address?: string;
    website?: string;
    contactForm?: string;
    rssUrl?: string;
    stateRank?: 'junior' | 'senior';
    class?: number; // Senate class
  };

  // Social media presence
  socialMedia?: {
    twitter?: string;
    facebook?: string;
    youtube?: string;
    instagram?: string;
    mastodon?: string;
  };

  // Cross-platform IDs
  ids?: {
    govtrack?: number;
    opensecrets?: string;
    votesmart?: number;
    fec?: string[];
    cspan?: number;
    wikipedia?: string;
    wikidata?: string;
    ballotpedia?: string;
  };

  // Leadership roles
  leadershipRoles?: Array<{
    title: string;
    start: string;
    end?: string;
  }>;

  // Enhanced contact information
  contact?: {
    dcOffice?: {
      address?: string;
      phone?: string;
      fax?: string;
      hours?: string;
    };
    districtOffices?: Array<{
      address: string;
      phone?: string;
      fax?: string;
      hours?: string;
    }>;
    contactForm?: string;
    schedulingUrl?: string;
  };

  // Data source metadata
  metadata?: {
    lastUpdated: string;
    dataSources: Array<'congress.gov' | 'congress-legislators' | 'fec' | 'openstates'>;
    completeness?: {
      basicInfo: boolean;
      socialMedia: boolean;
      contact: boolean;
      committees: boolean;
      finance: boolean;
    };
  };
}

// Representative summary for list views
export interface RepresentativeSummary {
  bioguideId: string;
  name: string;
  party: string;
  state: string;
  district?: string;
  chamber: 'House' | 'Senate';
  title: string;
  imageUrl?: string;
  website?: string;
  phone?: string;
}

// Representative search result
export interface RepresentativeSearchResult extends RepresentativeSummary {
  relevanceScore?: number;
  matchedFields?: string[];
}

// Committee information
export interface CommitteeMembership {
  committeeId: string;
  name: string;
  fullName: string;
  role: 'Chair' | 'Vice Chair' | 'Ranking Member' | 'Member';
  subcommittees?: Array<{
    name: string;
    role: string;
  }>;
  startDate?: string;
  endDate?: string;
}

// Enhanced committee data
export interface EnhancedCommitteeInfo {
  current: CommitteeMembership[];
  historical: CommitteeMembership[];
  leadership: Array<{
    committee: string;
    role: string;
    startDate: string;
    endDate?: string;
  }>;
}

// Representative analytics data
export interface RepresentativeAnalytics {
