}

// Specific data types for batch responses
export interface VoteData {
  vote_id: string;
  position: string;
  date: string;
  bill: {
    title: string;
    number: string;
  };
}

export interface BillData {
  bill_id: string;
  title: string;
  number: string;
  congress: number;
  introduced_date: string;
  status: string;
}

export interface FinanceData {
  total_receipts?: number;
  total_disbursements?: number;
  cash_on_hand?: number;
  cycle: string;
}

export interface NewsData {
  title: string;
  url: string;
  published_date: string;
  source: string;
}

// Batch API response types
export interface BatchApiResponse {
  success: boolean;
  data: {
    votes?: VoteData[];
    bills?: BillData[];
    finance?: FinanceData;
    news?: NewsData[];
    profile?: EnhancedRepresentative;
    [key: string]: unknown;
  };
  errors: Record<string, string>;
  metadata: {
    timestamp: string;
    requestedEndpoints: string[];
    successfulEndpoints: string[];
    failedEndpoints: string[];
    totalTime: number;
  };
  executionTime: number;
}

// Enhanced type for batch API hook
export interface BatchApiHookResult {
  data: {
    votes?: VoteData[];
    bills?: BillData[];
    finance?: FinanceData;
    news?: NewsData[];
    profile?: EnhancedRepresentative;
    [key: string]: unknown;
  };
  loading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
  metadata?: BatchApiResponse['metadata'];
  partialErrors?: Record<string, string>;
}

// Profile data structure for batch response
export interface RepresentativeProfile {
  profile: EnhancedRepresentative;
  votes?: VoteData[];
  bills?: BillData[];
  finance?: FinanceData;
  news?: NewsData[];
  'party-alignment'?: {
    score: number;
    total_votes: number;
    party_line_votes: number;
  };
  committees?: CommitteeMembership[];
  leadership?: {
    positions: string[];
    current_role?: string;
  };
  district?: {
    id: string;
    state: string;
    number: string;
  };
}

// Individual endpoint response wrapper
export interface EndpointResponse<T = unknown> {
  success: boolean;
  data?: T;
  error?: string;
  cached?: boolean;
  executionTime?: number;
}
